import React, { useState } from 'react';
import { NavLink } from "react-router-dom";
import { motion } from 'framer-motion';
import '../styles/About.scss';

function About() {
    return (
        <div id='About'>
            <motion.img id='me' src='../img/me.png' alt='Photo of Jiyun Yu'  initial={{ y: -150, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ y: { type: "spring", stiffness: 200, duration: 1 } }}/>
            <div className='about-container'>
                <h1>Hi, my name is Jiyun Yu.</h1>
                <h3>I'm a &nbsp;
                    <span><img id='front-end' src='../img/front-end.png' alt='front-end developer' /></span>
                    &nbsp; in &nbsp;
                    <span><img id='seattle' src='../img/seattle.png' alt='seattle, washington' /></span>
                    <br/>
                    focused on web/mobile design.
                </h3>
                <p>
                    I created this website to showcase my passion for front-end development, UX/UI design, 
                    and the research that comes along with my projects. Also, I’m a graduating senior at 
                    the University of Washington in Seattle and I don’t know what else to do with my spare time.
                </p>
                <h3>People have told me that I am </h3>
            </div>
            <div className='button-container'>
                <a href="" class="button" download>
                    <motion.img id='resume' 
                        src='../img/resume-button.png' alt='click for resume' 
                        animate={{ x: 0, rotate: 360 }} 
                        transition={{ x: { type: "easeOut" }, duration: 2 }} />
                </a>
                <NavLink id='contact' to="/contact">
                    <motion.img id='contact' src='../img/contact-button.png' alt='click for contact' />
                </NavLink>
                <button>
                    <motion.img id='fun' src='../img/fun-button.png' alt='click for fun' />
                </button>
            </div>
        </div>
    );
}

export default About;
