import React from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  NavLink
} from "react-router-dom";
import Home from './pages/Home';
import './styles/App.scss';

function App() {
  return (
    <div id="App">
      <Router>
        <header className="navigation">
          <Burger />
          <img className='logo' src='./img/flower-logo.png' alt='logo' />
          <Menu />
        </header>
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/projects/:id" />
          <Route path="/about" />
          <Route path="/contact" />
        </Switch>
        <footer>
          <p>by Jiyun Yu.</p>
        </footer>
      </Router>
    </div>
  );
}

function Burger() {
  const toggleBurger = () => {
    const oldClassName = document.getElementById('burger').className;
    const newClassName = oldClassName === 'burger menu' ? 'burger menu open' : 'burger menu'
    document.getElementById('overlay').style.height = oldClassName === 'burger menu' ? "100vh" : '0vh';
    document.body.style.overflow = oldClassName === 'burger menu' ? 'hidden' : 'show';
    document.getElementById('burger').className = newClassName
  }

  return (
    <div className='burger-container'>
      <div id='burger' className="burger" onClick={toggleBurger}>
        <div className="icon"></div>
      </div>
      <div id='overlay' className='menu-overlay'>
        <div className='mobile-container'>
          <NavLink id='projects' exact to="/">
            <span className='bullet'>•&nbsp;</span> projects
          </NavLink>
          <NavLink id='about' to="/about">
            <span className='bullet'>•&nbsp;</span> about me
          </NavLink>
          <NavLink id='contact' to="/contact">
            <span className='bullet'>•&nbsp;</span> contact
          </NavLink>
        </div>
      </div>
    </div>
  );
}

function Menu() {
  return (
    <div className='nav-container'>
      <NavLink id='projects' exact to="/">
        <span className='bullet'>•&nbsp;</span> projects
      </NavLink>
      <NavLink id='about' to="/about">
        <span className='bullet'>•&nbsp;</span> about me
      </NavLink>
      <NavLink id='contact' to="/contact">
        <span className='bullet'>•&nbsp;</span> contact
      </NavLink>
    </div>
  );
}

export default App;
