import React, { useState } from 'react';
import { NavLink } from "react-router-dom";
import { motion } from 'framer-motion';
import { Frame } from "framer";
import '../styles/About.scss';

function About() {
    const containerVariants = {
        before: {},
        after: { transition: { staggerChildren: 0.06 } },
    }
    const letterVariants = {
        before: {
          opacity: 0,
          y: 20,
          transition: {
              type: "spring",
              damping: 16,
              stiffness: 200,
          },
        },
        after: {
          opacity: 1,
          y: 0,
          transition: {
              type: "spring",
              damping: 16,
              stiffness: 200,
          },
        },
    }
    const string = 
    ['../img/about/words/confident.png', ]; 

    document.body.style.overflow = 'hidden'

    return (
        <div id='About'>
            <motion.img id='me' src='../img/about/me.png' alt='Photo of Jiyun Yu'  initial={{ y: -150, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ y: { type: "spring", stiffness: 200, duration: 1 } }}/>
            <div className='about-container'>
                <motion.h1  
                    initial={{ opacity: 0 }} 
                    animate={{ opacity: 1 }} 
                    transition={{ duration: 1.75 }} >
                    Hi, my name is <img id='my-name' src='../img/about/jiyun.png' alt='Jiyun Yu' />.
                </motion.h1>
                <motion.div 
                    initial={{ opacity: 0 }} 
                    animate={{ opacity: 1 }} 
                    transition={{ delay: 0.35, duration: 1.25 }} > 
                    <h3>I'm a &nbsp;
                        <span><img id='front-end' src='../img/about/front-end.png' alt='front-end developer' /></span>
                        &nbsp; in &nbsp;
                        <span><img id='seattle' src='../img/about/seattle.png' alt='seattle, washington' /></span>
                        <br/>
                        focused on web/mobile design.
                    </h3>
                    <p>
                        I created this website to showcase my passion for front-end development, UX/UI design, 
                        and the research that comes along with my projects. I’m also a graduating senior at 
                        the University of Washington in Seattle and I don’t know what else to do with my spare time.
                    </p>
                </motion.div>
                <motion.h3
                 initial={{ opacity: 0 }} 
                 animate={{ opacity: 1 }} 
                 transition={{ delay: 0.65, duration: 1.25 }} >
                    People have told me that I am 
                    <Frame
                        height={ 30 }
                        background={''}
                        width={ "10%" }
                        style={{
                            display: "inline", // Set the display value to flex
                        }}
                        variants={ containerVariants }
                        initial={ "before" }
                        animate={ "after" }
                        >
                        {string.map((word, index) => (
                            <Frame
                            key={ index }
                            background={''}
                            width={ "auto" } // Set the width to the width of the letter
                            height={ 30 } // Set the height to the height of the text
                            style={{ display: "inline", position: "relative" }}
                            variants={ letterVariants }
                            >
                                { index < 1 && <img className='word' src={ word } alt='confident' /> }
                            </Frame>
                         ))}
                    </Frame>
                </motion.h3>
                <div className='button-container'>
                    <motion.a href="" download
                        whileHover={{
                            rotate: 360,
                            transition: { duration: 1 },
                        }} >
                        <motion.img id='resume' 
                            src='../img/about/resume-button.png' alt='click for resume' 
                            initial={{ x: '50vw' }} 
                            animate={{ x: 0, rotate: 360 }} 
                            transition={{ duration: 1 }}  />
                    </motion.a>
                    <motion.div
                        whileHover={{
                            rotate: 360,
                            transition: { duration: 1 },
                        }} >
                        <NavLink id='contact' to="/contact">
                            <motion.img id='contact' 
                                src='../img/about/contact-button.png' alt='click for contact'
                                initial={{ x: '50vw' }} 
                                animate={{ x: 0, rotate: 360 }} 
                                transition={{ delay: 0.35, duration: 1 }}
                                whileHover={{
                                    rotate: 360,
                                    transition: { duration: 1 },
                                }} />
                        </NavLink>
                    </motion.div>
                    <motion.button 
                        whileHover={{
                            rotate: 360,
                            transition: { duration: 1 },
                        }} >
                        <motion.img id='fun' 
                            src='../img/about/fun-button.png' alt='click for fun'
                            initial={{ x: '50vw' }} 
                            animate={{ x: 0, rotate: 360 }} 
                            transition={{ delay: 0.65, duration: 1 }}
                            whileHover={{
                                rotate: 360,
                                transition: { duration: 1 },
                            }} />
                    </motion.button>
                </div>
            </div>
        </div>
    );
}

export default About;
